import React from 'react';
import Stack from '@mui/material/Stack';
import ButtonGroup from './ButtonGroup';
import Searchbar from './Searchbar';
import SlideList from './SlideList';

const SideBar = ({ onClick, onSlideOpen }) => {

    const [slideDecks, setSlideDecks] = useState([]);

    const handleRefresh = () => {
        try {
            const response = fetch('/api/slide-decks');
            if (!response.ok) {
                throw new Error('Failed to fetch slide decks');
            }
            const data = response.json();
            setSlideDecks(data.slideDecks);
        } catch (error) {
            console.error('Error fetching slide decks: ', error);
        }
    };

    return (
        <div style={{ display: 'inline-block', height: '570px', width: '350px', padding: 10, border: '1px solid gray' }}>
            <Stack>
              <ButtonGroup onComplete={handleRefresh} />
              <Searchbar />
              <SlideList onClick={onClick} onSlideOpen={onSlideOpen} decks={handleRefresh} />
            </Stack>
        </div>
    );
};

export default SideBar;
