import React, { useState } from 'react';
import Input from '@mui/material/Input';
import { Stack, Button } from '@mui/material';


function TestButton() {
    const [file, setSelectedFile] = useState(null);
    const [message, setMessage] = useState('');

    const handleFileChange = (e) => {
        if (e.target.files) {
            setMessage('');
            setSelectedFile(e.target.files[0]);
          }
    };

    const handleUpload = async (event) => {

        if (!file) {
            setMessage('Please select a file.');
            return;
        }
        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await fetch('/api/upload-doc', {
              method: 'POST',
              body: formData,
            });

            if (response.ok) {
              const data = await response.json();
              setMessage('Success!')
            } else {
              // Handle errors
              setMessage('Failed');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <Stack spacing={1} direction='row' style={{ alignItems: 'center', backgroundColor: 'lightgray' }}>
            <Input type="file" disableUnderline style={{ width:'180px', fontSize: '12px', height: '25px' }} onChange={handleFileChange} />
            <Button size='small' variant='outlined' style={{ height: '25px', fontSize: '12px', padding: '0px', marginTop: '3px', fill: 'black' }} onClick={handleUpload}>Upload</Button>
        </Stack>  
    );
};

export default TestButton;
